for name in `grep -h -o -e LOFAT-PLACE-FILE-CORRECTNESS-LEMMA-\[\[\:digit\:\]\]\\\+ lofat/lofat-place-file@useless-runes.lsp | sort | uniq` ; do echo "$name" ; find . -type d \( -path \*/SCCS -o -path \*/\{arch\} \) -prune -o \! -type d \( -name .\#\* -o -name \*.fdb_latexmk \) -prune -o  -type f \( -name \*.out \) -exec grep --color -nH --null -e $name \{\} + | wc -l ; done
